import * as actionTypes from "./user-types";

const INITIAL_STATE={
    user:null
};

const userReducer = (state=INITIAL_STATE,action) => {
    switch(action.type){
        case actionTypes.USER_LOGIN:
        return{
            ...state,
            user:action.payload
        };
        case actionTypes.USER_LOGOUT:
        return{
            ...state,
            user:null
        };
        default:
        return state;
    }
}

export default userReducer;
