import * as actionTypes from "./shopping-types";

const INITIAL_STATE = {
  products: [
      {
          "id": 1,
          "name": "Raptors Jersy 2022/23",
          "slug": "Mr. Albert Larkin",
          "price": 5000,
          "description": "Dolorem voluptatem aut ducimus facilis accusantium. Nisi quibusdam accusamus hic esse aut debitis. Soluta quas perferendis dolor culpa veritatis voluptatem. Aliquam at ab provident id ipsa.",
          "quantity": "Alfonso Stokes",
          "image": "https://media.bleacherreport.com/image/upload/w_800,h_533,c_fill/v1635774603/j2l6jcq4aq6cqbzqrtyy.jpg",
          "featured": 0,
          "created_at": "2022-10-08T15:09:20.000000Z",
          "updated_at": "2022-10-08T15:09:20.000000Z"
      },
      {
          "id": 2,
          "name": "Brooklyn Jersy 2022/23",
          "slug": "Lenna Ryan MD",
          "price": 12220,
          "description": "Perferendis et eaque nostrum at sed. Dolorem quis in provident placeat accusamus quibusdam consequatur. Magni sed nisi reprehenderit sequi.",
          "quantity": "Jessika Barton",
          "image": "https://i.insider.com/61814a881037b10018155943?width=1000&format=jpeg&auto=webp",
          "featured": 1,
          "created_at": "2022-10-08T15:09:20.000000Z",
          "updated_at": "2022-10-08T15:09:20.000000Z"
      },
  ],
  cart: [],
  currentItem: null,
};

const shopReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case actionTypes.ADD_TO_CART:
      return {};
    case actionTypes.REMOVE_FROM_CART:
      return {};
    case actionTypes.ADJUST_QTY:
      return {};
    case actionTypes.LOAD_CURRENT_ITEM:
      return {};
    default:
      return state;
  }
};

export default shopReducer;
