import useFetch from "../useFetch";

const Detail = ({id}) => {
    console.log(id);
    const {data:product}=useFetch('http://127.0.0.1:8000/api/product/'+id);
    console.log(product);
     return ( 
        <div className="row container-fluid justify-content-center align-items-center mt-5">
        <div className="col-4">
            <p
                style={{"fontSize":"35px","width":"20ch","textAlign":"center","margin":"0 auto","fontFamily":"Varela Round","textTransform":"uppercase","color":"#110d0dc9"}}>
                {product.name }</p>
            <div style={{"height":"2px","backgroundColor":"black","width":"40px","margin":"8px auto"}}></div>

            <p
                style={{"fontSize":"20px","textAlign":"center","margin":"0 auto","fontFamily":"Varela Round","textTransform":"uppercase","color":"#110d0dc9"}}>
               RS {product.price}</p>
            <p className="mt-5" style={{"fontSize":"20px","margin":"0 auto","fontFamily":"Varela Round","color":"#110d0dc9"}}>{product.description}</p>
            <form action="/add_to_cart/{{ $product.slug }}" method="post" className="form mt-4">
                @csrf
                <input type="number" value="1" name="quantity" min="1" style={{width: "45px"}} />
                <button type="submit" className="btn btn-md btn-dark w-75 ms-5">Add to Cart</button>
            </form>
        </div>
        <img className="col-6" src={ product.image } style={{"width":"650px","height":"400px","objectFit":"cover"}} />
    </div>
     );
}

export default Detail;
