const SingleCard = () => {
    return ( 
        <div className="card col-4 mt-5" style={{width: '300px', border: 'none'}}>
        <a href="/details/{{$product->id}}" className="text-decoration-none">
            <span style={{position: 'absolute', left: '20px', top: '10px', backgroundcolor: 'rgb(51, 48, 29)', padding: '3px', color: 'white'}}>Featured</span>    
            <img src="{{ $product->image }}" className="card-img-top" style={{width: '400px', height: '300px', objectfit: 'cover'}} alt="..." />
        <div className="card-body text-center">
            <h5 className="card-title fs-3" style={{width: '20ch', fontFamily: 'Varela Round',textTransform: 'uppercase', color: '#110d0dc9'}}> Mero Jersey </h5>
            <p className="card-title" style={{fontFamily: 'Varela Round',texttransform: 'uppercase', color: '#110d0dc9'}}>Rs 20000</p>
        </div>
        </a>
      </div>
    )
}

export default SingleCard;
