import { connect } from "react-redux";

const Cart = ({ cart }) => {
  return (
    <div class="row continer-fluid justify-content-end me-5">
      <div class="col-6">
        <img
          style={{ maxWidth: "100%" }}
          src="https://img.freepik.com/premium-vector/moving-house-service-flat-vector-illustration-post-office-workers-loading-cardboard-boxes-into-truck-deliverymen-planning-parcels-shipment-isolated-cartoon-character-white-background_106317-11533.jpg?w=2000"
          alt="loading into truck"
        />
      </div>
      <div class="col-6">
        <table class="table">
          <thead>
            <td>Product</td>
            <td>Quantity</td>
            <td>Price</td>
            <td>Total Price</td>
          </thead>
          {/* start of loop */}
          {cart.map((item) => (
            <tr>
              <td>
                Jersey{" "}
                <a
                  href="cart/remove/{{ $product->id }}"
                  class="text-decoration-none text-danger"
                >
                  X
                </a>
              </td>
              <td>{item.qty}</td>
              <td>Rs. 123</td>
              <td>Rs. 1233</td>
            </tr>
          ))}
        </table>
        <div class="row justify-content-end">
          <a href="/checkout" class="btn btn-md btn-dark w-50 mt-4">
            Checkout
          </a>
        </div>
      </div>
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    cart: state.shop.cart,
  };
};

export default connect(mapStateToProps)(Cart);
