import { useEffect, useState } from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";

const Checkout = ({ cart, removeFromCart }) => {
    console.log(cart);
    const [totalPrice, setTotalPrice]=useState(0);
    const [totalItem, setTotalItem]=useState(0);

    //for form
    const [title, setTitle]=useState();
  const [body, setBody]=useState();
  const [author, setAuthor]=useState('ram');

    //end for form
    useEffect(()=>{
        let items=0;
        let price=0;

        cart.forEach(item => {
            items+=item.qty;
            price+=item.qty*item.price;
        });
        setTotalPrice(price);
        setTotalItem(items);
    },[cart,totalItem,totalPrice,setTotalItem,setTotalPrice])
  return (
    <div className="row continer-fluid justify-content-end me-5">
      <div className="col-6">
        <table className="table">
          <thead>
            <td>Product</td>
            <td>Quantity</td>
            <td>Price</td>
            <td>Total Price</td>
          </thead>
          {/* start of loop */}
          {cart.map((item) => (
            <tr>
              <td>
                {item.name}
              </td>
              <td>{item.qty}</td>
              <td>Rs{item.price}</td>
              <td>Rs. {item.price*item.qty}</td>
            </tr>
          ))}

          <tr className="border-top border-dark bg-primary text-white">
            <td>Total:</td>
            <td></td>
            <td></td>
            <td>Rs. {totalPrice}</td>
          </tr>
        </table>
      </div>
      <div class="col-6">
      <form onSubmit={handleSubmit()}>
                <legend>Checkout Form</legend>
                <input type="text" className=" mt-3 form-control" placeholder="Enter firstname" name="firstname"/>
                <input type="text" className=" mt-3 form-control" placeholder="Enter lastname" name="lastname" />
                <input type="email" className=" mt-3 form-control" placeholder="Enter email" name="email"  />
                <input type="number" className=" mt-3 form-control" placeholder="Enter mobile" name="mobile_num" />
                <input type="text" className=" mt-3 form-control" placeholder="Enter address" name="address" />
                <input type="submit" className=" mt-3 btn btn-md btn-dark" />
            </form>
      </div>
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    cart: state.shop.cart,
  };
};



export default connect(mapStateToProps)(Checkout);
