const SingleCard = () => {
    return ( 
        <div class="card col-4 mt-5" style="min-width: 300px; border: none;">
        <a href="/details/{{$product->id}}" class="text-decoration-none">
        @if($product->featured==1)
            <span style="position: absolute; left: 20px; top: 10px; background-color: rgb(51, 48, 29); padding: 3px; color: white;">Featured</span>    
        @endif
            <img src="{{ $product->image }}" class="card-img-top" style="width: 400px; height: 300px; object-fit: cover;" alt="..." />
        <div class="card-body text-center">
            <h5 class="card-title fs-3" style="width: 20ch;margin:0 auto; font-family: Varela Round;text-transform: uppercase; color: #110d0dc9;"> Mero Jersey </h5>
            <div style="height: 1px;background-color: black; width: 60px; margin: 8px auto;"></div>
            <p class="card-title" style="margin:0 auto; font-family: Varela Round;text-transform: uppercase; color: #110d0dc9;">Rs 20000</p>
        </div>
        </a>
      </div>
    )
}

export default SingleCard;
