import React from 'react';
import { classNames } from '../../utilities/css.js';
import styles from './AlphaStack.scss.js';

const AlphaStack = ({
  children,
  align = 'start',
  fullWidth,
  spacing = '4'
}) => {
  const className = classNames(styles.AlphaStack, fullWidth && styles.fullWidth);
  const style = {
    '--pc-stack-align': align ? `${align}` : '',
    ...(spacing ? {
      '--pc-stack-spacing': `var(--p-space-${spacing})`
    } : {})
  };
  return /*#__PURE__*/React.createElement("div", {
    className: className,
    style: style
  }, children);
};

export { AlphaStack };
